                if (pos == "OPEN") { if (global.get(F_name+"_moving") == "UP") {nmsg.payload = "STOP";} else {nmsg.payload = pos;}}
                if (pos == "CLOSE") { if (global.get(F_name+"_moving") == "DOWN") {nmsg.payload = "STOP";} else {nmsg.payload = pos;}} 
                if (pos == "STOP") {nmsg.payload = "STOP";}
            }
            nmsg.topic = global.get("setup_prefix", "file")+F_name+"/set/state"            
        } else {
            nmsg.payload = Math.abs(pos);
            nmsg.topic = global.get("setup_prefix", "file")+F_name+"/set/position";
        }
